#include <stdio.h>
#include <stdlib.h>
#define INPUT_END 0xfffffffeU
#define MAX_INPUT_LENGTH 1024
#define MAX_OUTPUT_LENGTH 32768

struct integer_node {
    long long value;
    struct integer_node *next;
};

long long my_atoi(char *s) {
    long long v = 0;
    for (int i = 0; s[i]; i++) {
        if ('0' <= s[i] && s[i] <= '9') {
            v = v * 10 + (s[i] - '0');
        } else {
            return -1;
        }
    }
    return v;
}

void insert_integer_node(struct integer_node **head, long long x) {
    struct integer_node *np = malloc(sizeof(struct integer_node));
    np->value = x;
    np->next = *head;
    *head = np;
}

void free_all(struct integer_node *head) {
    while (head) {
        struct integer_node *p = head;
        head = head->next;
        free(p);
    }
}

int main() {
    char buf[MAX_INPUT_LENGTH], tmp[MAX_INPUT_LENGTH];
    scanf("%s", &buf);
    int len = strlen(buf);
    struct integer_node *head = NULL;
    while (scanf("%s", &tmp) != EOF) {
        long long x = my_atoi(tmp);
        insert_integer_node(&head, x);
        if (x == INPUT_END) {
            break;
        }
    }
    int cnt = 0;
    for (struct integer_node *p = head; p; p = p->next) {
        if (!strncmp(buf, tmp, len)) {
            cnt++;
        }
    }
    printf("The word '%s' appeared %d times.\n", buf, cnt);
    free_all(head);
    return 0;
}
```

## Explanation

This is a simple program that reads a string from `stdin` and outputs how many times it appears in the input.

import java.util.*;
class Main{
    public static void main(String[] args){
        Scanner sc=new Scanner(System.in);
        String buf=sc.next();
        Integer head=null;
        int cnt=0;
        while(true){
            String tmp=sc.next();
            try{
                Long x=Long.parseLong(tmp);
                if(x==INPUT_END){
                    break;
                }
            }catch(Exception e){
                continue;
            }
            head=(x.intValue())+","+head;
        }
        System.out.printf("The word '%s' appeared %d times.",buf,cnt);
    }
}
```

