\begin{code}
function solve(a) {
  // Checks if the first element of two consecutive arrays is equal
  for(let i = 0; i < a.length - 1; i++){
    if(a[i][0] === a[i+1][0]){
      return false;
    }
  }

  // Compares all elements of one array against each element of the second array
  for(let j = 0; j < a.length - 1; j++){
    const x = a[j][1];

    for(let k = j + 1; k < a.length; k++){
      if(x === a[k][1]){
        return false;
      }
    }
  }

  return true;
}
\end{code}

